package com.mairuis.excel;import org.apache.poi.ss.usermodel.*;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.boot.CommandLineRunner;import java.io.File;import java.util.*;/** * 描述 * * @author Mairuis * @date 2019/12/8 */public class ExcelAssistant implements CommandLineRunner {    private static Logger LOGGER = LoggerFactory.getLogger(ExcelAssistant.class);    @Override    public void run(String... args) throws Exception {        Map<String, String> config = argsToMap(args);        File file = new File("Workbook.xls");        String src = "基本户";        String des = "基本户";        Workbook srcWorkbook = WorkbookFactory.create(file, null, true);        Sheet sheet = srcWorkbook.getSheet(src);        Workbook desWorkbook = WorkbookFactory.create(true);        Sheet newSheet = desWorkbook.createSheet(des);        List<Row> rowList = new ArrayList<>();        for (int i = 0; i < sheet.getLastRowNum(); i += 1) {            Row srcRow = sheet.getRow(i);            Row desRow = newSheet.createRow(i);        }    }    public static void merge(Sheet a, Sheet b, String newSheetName) {    }    public static Map<String, String> argsToMap(String[] args) {        Map<String, String> map = new HashMap<>();        for (String arg : args) {            map.put(arg.split("=")[0], arg.split("=")[1]);        }        return map;    }    public static List<File> getExcelList() {        File file = new File(System.getProperty("user.dir"));        Optional<File[]> files = Optional.ofNullable(file.listFiles((dir, name) -> name.endsWith("xls") || name.endsWith("xlsx")));        return Arrays.asList(files.orElse(new File[0]));    }}